import UIKit

protocol UTMBusinessLogic
{
  func doSomething(request: UTM.Something.Request)
}

protocol UTMDataStore
{
  //var name: String { get set }
}

class UTMInteractor: UTMBusinessLogic, UTMDataStore
{
  var presenter: UTMPresentationLogic?
  var worker: UTMWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: UTM.Something.Request)
  {
    worker = UTMWorker()
    worker?.doSomeWork()
    
    let response = UTM.Something.Response()
    presenter?.presentSomething(response: response)
  }
}
