import UIKit

protocol UTMPresentationLogic
{
  func presentSomething(response: UTM.Something.Response)
}

class UTMPresenter: UTMPresentationLogic
{
  weak var viewController: UTMDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: UTM.Something.Response)
  {
    let viewModel = UTM.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
}
