import UIKit
import WebKit

protocol UTMDisplayLogic: AnyObject
{
  func displaySomething(viewModel: UTM.Something.ViewModel)
}

class UTMViewController: UIViewController, UTMDisplayLogic
{
  var interactor: UTMBusinessLogic?
  var router: (NSObjectProtocol & UTMRoutingLogic & UTMDataPassing)?
    @IBOutlet weak var webview: WKWebView!
    var url:String?
  // MARK: Object lifecycle
  
  override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
  {
    super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
    setup()
  }
  
  required init?(coder aDecoder: NSCoder)
  {
    super.init(coder: aDecoder)
    setup()
  }
  
  // MARK: Setup
  
  private func setup()
  {
    let viewController = self
    let interactor = UTMInteractor()
    let presenter = UTMPresenter()
    let router = UTMRouter()
    viewController.interactor = interactor
    viewController.router = router
    interactor.presenter = presenter
    presenter.viewController = viewController
    router.viewController = viewController
    router.dataStore = interactor
  }
  
  // MARK: Routing
  
  override func prepare(for segue: UIStoryboardSegue, sender: Any?)
  {
    if let scene = segue.identifier {
      let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
      if let router = router, router.responds(to: selector) {
        router.perform(selector, with: segue)
      }
    }
  }
  
  // MARK: View lifecycle
  
  override func viewDidLoad()
  {
    super.viewDidLoad()
    doSomething()
  }
    @IBAction func backPressed(_ sender: Any) {
        let storyBoard: UIStoryboard = UIStoryboard(name: "LoanRequest", bundle: nil)
                let newViewController = storyBoard.instantiateViewController(withIdentifier: "last") as! FinishLoanViewController
                        self.present(newViewController, animated: true, completion: nil)
    }
    
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        print("url recibida ::: \(url)")
        url = url!.replacingOccurrences(of: " ", with: "")
        print("url ajustada ::: \(url)")
        webview.load(NSURLRequest(url: NSURL(string: url!)! as URL) as URLRequest)
        webview.allowsBackForwardNavigationGestures = true
    }
  // MARK: Do something
  
  //@IBOutlet weak var nameTextField: UITextField!
  
  func doSomething()
  {
    let request = UTM.Something.Request()
    interactor?.doSomething(request: request)
  }
  
  func displaySomething(viewModel: UTM.Something.ViewModel)
  {
    //nameTextField.text = viewModel.name
  }
}
